        [Test]
        public void TokenExpiration()
        {

            string response = @"{
    ""access_token"": ""eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJNTGNwamN3ck9uZnNHZWFheXZ1UVppaVZ5RjRhREVweE1XdU1mdGdScU5rIn0..AH34kCBwfPbqpXHevgyatejxtIvwZCw11wae_P_1qSKP-1EAhSYDqpepINycnORL7PBqEcbq841ohp-Ih-kPVN7K8MLDIPkHZM-FbTh5BxddjlQT0f_O7inglpwDtNNRCGr3gvVClv4eQJ1HPktyeispiFfLqlXvrDgD9_I4TPuRqRSa_fmY2wGDrMtIFSPWn1DGdpV7_-_vOxMs1eJGPH71Ghvlkhgkywccd3Gvl6RPA8L8wq4bDIlJ4v-5LoyBnznZJEalb4lPiEgOhxHLmRjUEcmXUcKhG7uaf7EgN61XqNhmVBVuTLM3gCH44EFSsNER9B3_6Am1SM_z8P1Lhg"",
    ""expires_in"": 299,
    ""refresh_expires_in"": 1800,
    ""refresh_token"": ""eyJhbGciOiJIUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICI5NzgwMjU4Yi05MTVhLTQ3YzctYTA1Ni02YTkyOGY0MTc1MzkifQ..OlBx9v0iRviNuCjcYM42SzvrKcpKfGgWpDE-Brf5hvc"",
    ""token_type"": ""Bearer"",
    ""id_token"": ""eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJNTGNwamN3ck9uZnNHZWFheXZ1UVppaVZ5RjRhREVweE1XdU1mdGdScU5rIn0..ZN4zNnZ_dUeIBs51vi-EFD615J0RkVxG3eQ7Sy0Rv5dHFLE2t-xx7zLE_CXytQtbGe_JCzbmH9KbF9pPQixzuJ_Nq5fR0zHU1SwRK3eetpMIq8KjzHnbTL0SMwMuDsBevkMGIXeBry1CGLInbJHkSayq8vE7XhL0tDmd9od-9mczAeXOILX7Z2n06Iu6V8Ja6BmGYCcOsibvV94alahcmg9mIhRcr4Jlg2Bb3La1zCAHhcFU6qvuIvxLZ-gakmcgQ7EqY7zqEgsDIVc1-o1SxE7owt3qU79PYRvjv1cp6u5s3pLBO9xZZoGcKIybsEXxf6bTB6ZVQXkdnWmguHzDXQ"",
    ""not-before-policy"": 1656576311,
    ""session_state"": ""3c42c395-b012-4a1c-a79a-44f51d338d8e"",
    ""scope"": ""openid profile email""
}";

            var ti = TokenInfo.FromResponse(response, "packages.opentap.io");
            DateTime dateTime = new DateTime(2022, 7, 5, 11, 02, 56);
            TimeSpan timeSpan = ti.Expiration - dateTime;
            Console.WriteLine(dateTime.ToString());
            Console.WriteLine(ti.Expiration.ToString());
            Console.WriteLine(timeSpan.ToString());

            Assert.IsTrue(timeSpan.Days == 0);
            Assert.IsTrue(timeSpan.Hours == 0);
            Assert.IsTrue(timeSpan.Minutes == 0);
            Assert.IsTrue(timeSpan.Seconds == 0);
        }

        [Test]
        public void ParseTokenWithUrlChars()
