            // This token has a '-' and '_' in the payload which is not in the normal base64 char set. It is in the base64-URL charset though, and should work.
            string accessToken = "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICJlQUFuOTYwbFpXYU1tZGliTDg4Q29CVlhZSy1VcEhTeWE0T3Z3d04tQzI4In0.-IDliODM_NTNjIiwicHJlZmVycmVkX3VzZXJuYW1lIjoiYXNnZXIiLCJnaXZlbl9uYW1lIjoifn4iLCJmYW1pbHlfbmFtZSI6IjliODM_NTNjIn0.elXy3abQHHL9-hlVOfkH1JxzgZXyiRSI8JpVJgbiFic7A9fY0qFiUC6aBrR9_FNDU7zh3A4rCAmprdbonMwFRzkRnWfnipXgPTnAtFz9q2i6M0Tcnj-AAgPvZ9sjwtKdOKyzoqoKpEsfdiFYZb31oc8M4R7dRFAixPh8ARv9Lpzx5Hnu7q7A_ewOStQWZbqD-GQvtJyslkbXJM3RaTT3VpDRSXWr67SoIff9SxcrHAJpj_gJcwrg5xrZW4IdmE87_L3LcFvaLzeUx8IvrfpmVHuR8E8yR8RMu2oxiBXD5M1LJCbD3Wx6dTszqFRUOlnR1FA4xAsSgJ8Xba4MB5PWNA";
            var t = new TokenInfo(accessToken, null, "localhost");
            Assert.AreEqual("~~ 9b83?53c", t.Claims["name"]);
            Assert.AreEqual("1", t.Claims["acr"]);
